<?php$userData = $dataAndView->getData("userData");?><script nonce = "<?= $nonce_hash ?>">    $(function () {        var service = {};        service.new_password = function (contrasena, confirmar_contrasena) {            return $.ajax({                type: "post",                url: "/editar_perfil/save_new_password",                data: {                    password: contrasena,                    confirm_password: confirmar_contrasena                }            })        };        $("body").on("click", "#save_profile", function (e) {            e.preventDefault();            if(vacio('#new_password','El campo nueva contrase침a')){return false}            if(vacio('#confirm_password','El campo cotrase침a de confirmaci칩n')){return false}                var password_nuevo = $("#new_password").val();                var password_confirmacion = $("#confirm_password").val();            if (password_nuevo != password_confirmacion){                noty.show('danger','Las contrase침as no coninciden');                return false;            }            $.when(service.new_password( password_nuevo, password_confirmacion)).then(function (response) {                    noty.show(response.type, response.message);                    if(response.type == "success"){                        $("#new_password").val("");                        $("#confirm_password").val("");                    }            })        });        $('body').on('click','#cancel-edit',function () {            window.history.back();        });    });</script>